import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Card } from '../../models/card';
import DemoCard from 'react-bootstrap/Card';
import './style.css'

const CardComponent = () => {
    const [products, setProducts] = useState([]);
    const [count, setCount] = useState(0);
    // const handleClickADD = () => {
    //     setCount(count + 1);
    //     card.quantity = count;
    // }
    // const handleClickLESS = () => {
    //     if (count > 0) {
    //         setCount(count - 1);
    //     }
    //     card.quantity = count;
    // }

    setTimeout(() => {
        setProducts([
            {
                id: 1,
                image: '../../assets/Unknown.jpeg',
                name: 'Producto 1',
                description: 'Producto 2',
                quantity: 1,
            },
            {
                id: 2,
                image: '../../assets/Unknown.jpeg',
                name: 'Producto 2',
                description: 'Producto 3',
                quantity: 1,
            }])
    })
    return (
        <>
            {products.map((product) => (
                <DemoCard className='democard' key={product.id}>
                    <DemoCard.Img variant="top" src={product.image} />
                    <DemoCard.Body className='democardbody'>
                        <DemoCard.Title>{product.name}</DemoCard.Title>
                        <DemoCard.Text>
                            {product.description}
                        </DemoCard.Text>
                        <DemoCard.Text>
                            <button className='btn_count_less'>-</button>
                            {product.quantity}
                            <button className='btn_count_add'>+</button>
                        </DemoCard.Text>
                        <button className='btn_add' >Buy</button>
                    </DemoCard.Body>
                </DemoCard>
            ))}
        </>

    );
};


CardComponent.propTypes = {
    // card: PropTypes.instanceOf(Card)
};


export default CardComponent;
