import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Card } from '../../models/card';
import DemoCard from 'react-bootstrap/Card';
import './style.css'

const CardComponent = ({ card }) => {
    const [products, setProducts] = useState([]);
    const [count, setCount] = useState(0);
    const handleClickADD = () => {
        setCount(count + 1);
        card.quantity = count;
    }
    const handleClickLESS = () => {
        if (count > 0) {
            setCount(count - 1);
        }
        card.quantity = count;
    }

    setTimeout(() => {
        setProducts = ([
            {
                image: '../assets/Unknown.jpeg',
                name: 'Producto 1',
                description: 'Producto 2',
                quantity: 1,
            },
            {
                image: '../assets/Unknown.jpeg',
                name: 'Producto 2',
                description: 'Producto 3',
                quantity: 1,
            }])
    })
    return (
        <>
            <DemoCard className='democard' >
                <DemoCard.Img variant="top" src={card.image} />
                <DemoCard.Body className='democardbody'>
                    <DemoCard.Title>{card.name}</DemoCard.Title>
                    <DemoCard.Text>
                        {card.description}
                    </DemoCard.Text>
                    <DemoCard.Text>
                        <button onClick={handleClickLESS} className='btn_count_less'>-</button>
                        {card.quantity}
                        <button onClick={handleClickADD} className='btn_count_add'>+</button>
                    </DemoCard.Text>
                    <button className='btn_add' >Buy</button>
                </DemoCard.Body>
            </DemoCard>
        </>

    );
};


CardComponent.propTypes = {
    card: PropTypes.instanceOf(Card)
};


export default CardComponent;
