import React, { useState, useEffect } from 'react';

const CountInput = (props) => {
    return (
        <div>
            <div>
                <input type='button' onClick={props.onConfirm} className='btn_count_add' value={props.value} />
            </div>
        </div>
    )
}

const ItemCount = () => {
    const [count, setCount] = useState(0);
    const handleAdd = (value) => {
        setCount(value + 1);
    }
    const handleLess = (value) => {
        if (count > 1) { setCount(value - 1); }
    }

    const handleClick = () => onAdd(count);
    useEffect(() => {
        setCount(parseInt(initial));
    }, [initial])

    return (
        <div>
            <h3>Item Comprados</h3>
            <h4>{count}</h4>
            <h4>Cantidad Disponible: </h4>
            <div>
                <CountInput onConfirm={() => handleAdd(count)} className='btn_count_add' value='+' title='Disponible' >+</CountInput>
            </div>
            <div>
                <CountInput onConfirm={() => handleLess(count)} className='btn_count_add' value='-'>-</CountInput>
            </div>
        </div>
    )
}

export default ItemCount;
