import React, { useContext, useState } from 'react';
import { Container, Button, Table, Modal, Form } from 'react-bootstrap';
import { FaTrashAlt } from "react-icons/fa";
import { Link } from 'react-router-dom';
import CartContext from '../../contexts/CartContext';
import './style.css'


const buyerMock = {
    name: 'Usuario',
    phone: '123456789',
    email: 'test@gmail.com'
}

const Cart = () => {

    const { cart, total, removeItem } = useContext(CartContext);
    const [user, setSer] = useState(buyerMock);
    const [showmodal, setShowModal] = useState(false);
    console.log({ cart, total });

    const handleRemove = (itemId) => {
        removeItem(itemId);
    }

    const handleOpen = () => setShowModal(true);

    const handleClose = () => setShowModal(false);


    const showTable = cart.length > 0
    return (
        <Container className='cartContainer'>
            <h1>Carrito de Compras</h1>
            {showTable && (
                <>
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>Titulo</th>
                                <th>Precio</th>
                                <th>Cantidad</th>
                                <th>Accion</th>
                            </tr>
                        </thead>
                        <tbody>
                            {cart.map((item) => (
                                <tr key={item.id}>
                                    <td>{item.title}</td>
                                    <td>{item.price}</td>
                                    <td>{item.quantity}</td>
                                    <td><FaTrashAlt className='iconFaTrashAlt' onClick={() => handleRemove(item.id)} /></td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                    <h3>Total: $ {total}</h3>
                    <Button variant='sucess' onClick={handleOpen}>Finalizar compra</Button>
                </>
            )}
            {!showTable && (
                <>
                    <h3>Carrito de compra vacio</h3>
                    <Link to='/'>
                        <Button variant="success">Ver productos</Button>
                    </Link>
                </>
            )}
            <Modal show={showmodal} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Finalizar Compra</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form.Group className="mb-3" controlId="formBasicEmail">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control type="email" placeholder="Ingrese email" />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formBasicEmail">
                        <Form.Label>Nombre</Form.Label>
                        <Form.Control type="text" placeholder="ingrese nombre" />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formBasicEmail">
                        <Form.Label>Telefono</Form.Label>
                        <Form.Control type="text" placeholder="Ingrese telefono" />
                    </Form.Group>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Cancelar
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Comprar
                    </Button>
                </Modal.Footer>
            </Modal>
        </Container>

    );
}

export default Cart;
