import React from 'react';
import DemoCard from 'react-bootstrap/Card';
import './style.css'

const ItemCard = ({ product }) => {

    return (
        <>

            <DemoCard className='democard'>
                <DemoCard.Img variant="top" src={product.pictureUrl} className="imagen" />
                <DemoCard.Body className='democardbody'>

                    <DivStyledAsH6 className='demoTitle'>{product.name}</DivStyledAsH6>
                    <DemoCard.Text className='demoDescription'>{product.description}</DemoCard.Text>
                    <DemoCard.Text className='demoPrice'>{product.price}</DemoCard.Text>
                    <button className='btn_count_less'> -</button>
                    {product.quantity}
                    <button className='btn_count_add'>+</button>
                    <DemoCard.Text>
                        <button className='btn_add' >Buy</button>
                    </DemoCard.Text>
                </DemoCard.Body>
            </DemoCard>
        </>
    );
};

export default ItemCard;
