import React, { useState } from 'react';

const CountInput = (props) => {
    return (
        <div>
            <div>
                {/* <div>{props.title}  : {props.restantes}</div> */}
                <input type='button' onClick={props.onConfirm} className='btn_count_add' value={props.value} />
            </div>
            {/* */}
        </div>
    )
}

const Intercambiabilidad = () => {
    const [count, setCount] = useState(0);
    const handleAdd = (value) => {
        setCount(value + 1);
    }
    const handleLess = (value) => {
        setCount(value - 1);
    }

    // const handleConfirm = (quantity) => {
    //     console.log(quantity)
    // }

    return (
        <div>
            <h3>Item Comprados</h3>
            <h4>{count}</h4>
            <h4>Cantidad Disponible: </h4>
            <div>
                <CountInput onConfirm={() => handleClick(count + 1)} className='btn_count_add' value='+' title='Disponible' >+</CountInput>
            </div>
            <div>
                <CountInput onConfirm={() => handleClick(count - 1)} className='btn_count_add' value='-'>-</CountInput>
            </div>
        </div>
    )
}

export default Intercambiabilidad;
