import React, { useState } from 'react';
import DemoCard from 'react-bootstrap/Card';
import './style.css'

const ItemCard = ({ product }) => {
    const [count, setCount] = useState(0);
    const handleClickADD = () => {
        setCount(count + 1);
        product.quantity = count;
    }
    const handleClickLESS = () => {
        if (count > 0) {
            setCount(count - 1);
        }
        product.quantity = count;
    }
    return (

        <DemoCard className='democard'>
            <DemoCard.Img variant="top" src={product.image} />
            <DemoCard.Body className='democardbody'>
                <DemoCard.Title name={product.name} ></DemoCard.Title>
                <DemoCard.Text name={product.description} >

                </DemoCard.Text>
                <DemoCard.Text>
                    <button onClick={handleClickLESS} className='btn_count_less' >-</button>
                    {product.quantity}
                    <button onClick={handleClickADD} className='btn_count_add'>+</button>
                </DemoCard.Text>
                <button className='btn_add' >Buy</button>
            </DemoCard.Body>
        </DemoCard>

    );
};

export default ItemCard;
