import React, { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';

const CountInput = (props) => {
    return (
        <div>
            <div>
                <input type='button' onClick={props.onConfirm} className='btn_count_add' value={props.value} disabled={props.stock} />
            </div>
        </div>
    )
}

const ItemCount = ({ initial, stock, onAdd }) => {
    const [count, setCount] = useState(parseInt(initial));
    const handleAdd = (value) => {
        setCount(value + 1);
    }
    const handleLess = (value) => {
        if (count > 1) { setCount(value - 1); }
    }

    const handleClick = () => onAdd(count);

    useEffect(() => {
        setCount(parseInt(initial));
    }, [initial])

    return (
        <div className='contenedorItemCount'>
            <div className='btnCount'>

                <div>
                    <Button onConfirm={() => handleAdd(count <= 0)} className='btn_count_add' value='+' title='Disponible' >+</Button>
                </div>
                <h5>{count}</h5>
                <div>
                    <Button onConfirm={() => handleLess(count)} className='btn_count_add' value='-'>-</Button>
                </div>
                <div>
                    <Button disable={stock <= 0} onClick={handleClick} >Agregar al carrito</Button>
                </div>
            </div>
        </div>
    )
}

export default ItemCount;
