import React, { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';

const CountInput = (props) => {
    return (
        <div>
            <div>
                <input type='button' onClick={props.onConfirm} className='btn_count_add' value={props.value} disabled={props.stock} />
            </div>
        </div>
    )
}

const ItemCount = ({ initial, stock, onAdd }) => {
    const [count, setCount] = useState(parseInt(initial));
    const handleAdd = () => {
        setCount(count + 1);
    }
    const handleLess = () => {
        if (count > 1) { setCount(count - 1); }
    }

    const handleClick = () => onAdd(count);

    useEffect(() => {
        setCount(parseInt(initial));
    }, [initial])

    return (
        // <div className='contenedorItemCount'>
        //     <div className='btnCount'>

        //         <Button onClick={handleAdd} className='btn_count_add' value='+' title='Disponible' >+</Button>

        //         <div><h5>{count}</h5></div>

        //         <Button onClick={handleLess} className='btn_count_add' value='-'>-</Button>

        //     </div>
        //     <div>
        //         <Button disable={stock <= 0} onClick={handleClick} >Agregar al carrito</Button>
        //     </div>
        // </div>
        <div className='itemCountContainer'>
            <div>
                <Button variant="danger" disabled={count <= 1} onClick={handleLess}>
                    -
                </Button>
                <h5>{count}</h5>
                <Button variant="success" disabled={count >= stock} onClick={handleAdd}>
                    +
                </Button>
            </div>
            <div>
                <Button variant="primary" disabled={stock <= 0} onClick={handleClick}>
                    Agregar al  Carrito
                </Button>
            </div>
        </div>
    )
}

export default ItemCount;
