import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Card } from '../../models/card';
import DemoCard from 'react-bootstrap/Card';
import './style.css'
import { data } from '../../data';

const CardComponent = () => {
    const [products, setProducts] = useState([]);
    const [count, setCount] = useState(0);
    const datas = fetch('../../data/index.js');
    const handleClickADD = () => {
        setCount(count + 1);
        products.quantity = count;
    }
    const handleClickLESS = () => {
        if (count > 0) {
            setCount(count - 1);
        }
        products.quantity = count;
    }

    datas.then((response) => {
        const element = response.json();
        element.then((item) => {
            // setTimeout(() => {
            //     setProducts(item);

            // }, "300")
            console.log(item[0])
        })
    })


    return (
        <>
            {products.map((product) => (
                <DemoCard className='democard' key={product.id}>
                    <DemoCard.Img key={product.image} variant="top" src={product.image} />
                    <DemoCard.Body className='democardbody'>
                        <DemoCard.Title key={product.name}>{product.name}</DemoCard.Title>
                        <DemoCard.Text key={product.description}>
                            {product.description}
                        </DemoCard.Text>
                        <DemoCard.Text>
                            <button onClick={handleClickLESS} className='btn_count_less' key={product.quantity}>-</button>
                            {product.quantity}
                            <button onClick={handleClickADD} className='btn_count_add'>+</button>
                        </DemoCard.Text>
                        <button className='btn_add' >Buy</button>
                    </DemoCard.Body>
                </DemoCard>
            ))}
        </>

    );
};


CardComponent.propTypes = {
    // card: PropTypes.instanceOf(Card)
};


export default CardComponent;
