import React, { useState } from 'react';



const CountInput = (props) => {
    return (
        <>
            <p>{props.title}  : {props.maxQuantity}</p>
            <div>
                <input type='button' onClick={props.onConfirm} className='btn_count_add' value={props.value} />
            </div>
            {/* */}
        </>
    )
}

const Intercambiabilidad = () => {
    const [count, setCount] = useState(0);
    const [cantidad, setCantidad] = useState(10);
    let maxQuantity = 10;
    let aux = 0;
    const handleClick = (value) => {
        if (value > 0) {
            // console.log(value);
            setCount(value);
            maxQuantity = maxQuantity - count;
            setCantidad(maxQuantity);
            console.log(maxQuantity);
            if (value >= 10) {
                setCount(10);
            }
        } else {
            setCount(0);
        }
    }

    const handleConfirm = (quantity) => {
        console.log(quantity)
    }

    return (
        <div>
            <h3>Item Comprados</h3>
            <h4>{count}</h4>
            <h4>Cantidad Disponible</h4>
            <h4>{cantidad}</h4>
            <CountInput onConfirm={() => handleClick(count + 1)} className='btn_count_add' value='+' title='Disponible'>+</CountInput>
            <CountInput onConfirm={() => handleClick(count - 1)} className='btn_count_add' value='-'>-</CountInput>
            {/* <button onClick={() => handleClick(count + 1)} className='btn_count_add'>+</button> */}
        </div>
    )
}

export default Intercambiabilidad;
