import ItemCard from '../item_Card';

const ItemDetail = ({ product }) => {


    return (
        <div>

            <button className='btn_count_less'> -</button>
            {product.quantity}
            <button className='btn_count_add'>+</button>
            <ItemCard product={product} />
        </div>
    );
}

export default ItemDetail;
