import React, { useState } from 'react';


const CountButton = ({ onConfirm, maxQuantity }) => {
    return (
        <>
            Disponible: {maxQuantity}
            <button onClick={onConfirm}>Agregar al carrito</button>
            {/* <button onClick={() => handleClick(count - 1)} className='btn_count_less'> -</button> */}
        </>
    )
}


const CountInput = ({ onConfirm, maxQuantity }) => {
    return (
        <>
            Disponible : {maxQuantity}
            <div>
                <input type='button' onClick={onConfirm} value='Agregar al carrito' />
            </div>
            {/* */}
        </>
    )
}

const type = ''
const Intercambiabilidad = () => {
    const [count, setCount] = useState(0);
    const [onGetProduct, setOnGetProduct] = useState(0);
    const handleClick = ({ onConfirm, maxQuantity }) => {
        if (onConfirm > 0) {
            setCount(onConfirm);
            maxQuantity = maxQuantity - 1
        } else {
            setCount(0);
            if (maxQuantity === 0) {
                maxQuantity = 0;
            }
        }
        // (value > 0) ? (setCount(value)) : (setCount(0));
    }
    const Count = (type === 'button') ? CountButton : CountInput;

    const handleConfirm = (quantity) => {
        console.log(quantity)
    }

    return (
        <>
            <h3>Item Description</h3>
            <h4>{count}</h4>
            <h4>{onGetProduct}</h4>
            <Count onConfirm={() => handleClick(onGetProduct + 1)} maxQuantity={10} />
            <Count onConfirm={() => handleClick(onGetProduct - 1)} maxQuantity={10} />
            <button onClick={() => handleClick(count + 1)} className='btn_count_add'>+</button>
        </>
    )
}

export default Intercambiabilidad;
