import React, { useState } from 'react';
import { propTypes } from 'react-bootstrap/esm/Image';


const CountButton = ({ onConfirm, maxQuantity }) => {
    return (
        <>
            Disponible: {maxQuantity}
            <button onClick={onConfirm}>Agregar al carrito</button>
            {/* <button onClick={() => handleClick(count - 1)} className='btn_count_less'> -</button> */}
        </>
    )
}


const CountInput = ({ props }) => {
    return (
        <>
            Disponible : {props}
            <div>
                <input type='button' className='btn_count_add'>{props.value}</input>
            </div>
            {/* */}
        </>
    )
}

const type = ''
const Intercambiabilidad = () => {
    const [count, setCount] = useState(0);
    const [cantidad, setCantidad] = useState(10);
    let maxQuantity = 10;
    let aux = 0;
    const handleClick = (value) => {
        if (value > 0) {
            // console.log(value);
            setCount(value);
            maxQuantity = maxQuantity - count;
            setCantidad(maxQuantity);
            console.log(maxQuantity);
            if (value >= 10) {
                setCount(10);
            }
        } else {
            setCount(0);
        }
        // (value > 0) ? (setCount(value)) : (setCount(0));
    }
    const Count = (type === 'button') ? CountButton : CountInput;

    const handleConfirm = (quantity) => {
        console.log(quantity)
    }

    return (
        <>
            <h3>Item Comprados</h3>
            <h4>{count}</h4>
            <h4>Cantidad Disponible</h4>
            <h4>{cantidad}</h4>
            <Count onClick={() => handleClick(count + 1)} className='btn_count_add' ></Count>
            <Count onClick={() => handleClick(count - 1)} className='btn_count_add' ></Count>
            <button onClick={() => handleClick(count + 1)} className='btn_count_add'>+</button>
        </>
    )
}

export default Intercambiabilidad;
