import img1 from '../assets/1.png';
import img2 from '../assets/2.png';
import img3 from '../assets/3.png';
import img4 from '../assets/5.png';
import img5 from '../assets/6.png'

export const products = [
    { id: 1, pictureUrl: `${img1}`, title: 'Producto 1', description: 'Lemon pie', price: 100, stock: 1, },
    { id: 2, pictureUrl: `${img2}`, title: 'Producto 2', description: 'Brownie', price: 100, stock: 1, },
    { id: 3, pictureUrl: `${img3}`, title: 'Producto 3', description: 'Tarta de frutos', price: 100, stock: 1, },
    { id: 4, pictureUrl: `${img4}`, title: 'Producto 4', description: '', price: 100, stock: 1, },
    { id: 5, pictureUrl: `${img5}`, title: 'Producto 5', description: 'Producto 5', price: 100, stock: 1, }
]

export const getProducts = () => {
    const promise = new Promise((resolve) => {
        setTimeout(() => {
            return resolve(products);
        }, 2000);
    })
    return promise;
}
export const getProduct = (id) => {
    const promise = new Promise((resolve) => {
        const result = products.find((product) => product.id === parseInt(id))
        setTimeout(() => {
            return resolve(result);
        }, 2000);
    })
    return promise;
}
